def db_put(sql_query):
    conn = pyodbc.connect('Driver={};Server={};Database={};{};'.format(db_params.driver,db_params.server,db_params.database,db_params.params))
    cursor = conn.cursor()
    cursor.execute(sql_query)
    cursor.close()
    conn.commit()
    conn.close()

def db_pull_val(sql_query):
    conn = pyodbc.connect('Driver={};Server={};Database={};{};'.format(db_params.driver, db_params.server, db_params.database, db_params.params))
    cursor = conn.cursor()
    cursor.execute(sql_query)
    value = cursor.fetchval()
    response = json.dump(value)
    cursor.close()
    conn.commit()
    conn.close()
    return response

def db_pull_list(sql_query):
    conn = pyodbc.connect('Driver={};Server={};Database={};{};'.format(db_params.driver, db_params.server, db_params.database, db_params.params))
    cursor = conn.cursor()
    cursor.execute(sql_query)
    list = [i[0] for i in cursor.fetchall()]
    response = json.dump(list)
    cursor.close()
    conn.commit()
    conn.close()
    return response